namespace Zink\Widget;

class WaterMark
{
    const WATER_BASED_64 = "data:image/png;base64,";


    public static function addMark($filePath)
    {
        if (!file_exists($filePath)) {
            return false;
        }

        list($waterWidth, $waterHeight) = @getimagesize(self::WATER_BASED_64);
        list($oriWidth, $oriHeight) = @getimagesize($filePath);
        $resultImg = @imagecreate($oriWidth, $oriHeight);
        imagecolortransparent($resultImg, imagecolorallocate($resultImg, 0, 0, 0));
        $waterImg = @imagecreatefrompng(self::WATER_BASED_64);
        $srcImg = @imagecreatefromstring(file_get_contents($filePath));
        @imagecopyresampled($resultImg, $waterImg, 0, 0, 0, 0, $oriWidth, $oriHeight, $waterWidth, $waterHeight);
        @imagecopy($srcImg, $resultImg, 0, 0, 0, 0, $oriWidth, $oriHeight);

        ob_start();
        @imagejpeg($srcImg);
        $buffer = ob_get_contents();
        ob_clean();
        return file_put_contents($filePath, $buffer);
    }

}